import React, { useState, useEffect } from 'react';
import { TextField, Button } from '@material-ui/core';
import axios from 'axios';

const Login = props => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [password2, setPassword2] = useState('');
  const [register, setRegister] = useState(false);


  //Material UI style

  const useStyles = makeStyles({
    textFie: {
      background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
      border: 0,
      borderRadius: 3,
      boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
      color: 'white',
      height: 48,
      padding: '0 30px',
    },
  });

  const handleSubmit = e => {
    e.preventDefault();
    //create object with credentials
    const loginCredentials = {
      username: username,
      password: password
    };

    const signUpCredentials = {
      username: username,
      password1: password,
      password2: password2
    };

    //API Call here before clearing the fields
    if (register) {
      signUp(signUpCredentials);
    } else {
      signIn(loginCredentials);
    }
    //
    setUsername('');
    setPassword('');
  };

  function signIn(credentials) {
    axios
      .post(`https://lambda-mud-test.herokuapp.com/api/login/, ${credentials}`)
      .then(response => {
        console.log(response.data);
        // place token in local storage for axiosWithAuth to handle it
        localStorage.setItem('token', response.data);
        //redirect user to game
        // props.history.push('protected');
      })

      .catch(error => {
        console.log(error);
      });
  }

  function signUp(credentials) {
    axios
      .post(
        `https://lambda-mud-test.herokuapp.com/api/registration/, ${credentials}`
      )
      .then(response => {
        console.log(response.data);
        // place token in local storage for axiosWithAuth to handle it
        localStorage.setItem('token', response.data);
        //redirect user to game
        // props.history.push('protected');
      })

      .catch(error => {
        console.log(error);
      });
  }

  return (
    <div>
      <header className="App-header">
        <h1>{register ? 'Sign up' : 'Login'}</h1>
        <br />
        <form onSubmit={handleSubmit}>
          <TextField
            name="username"
            id="outlined-basic"
            label="Email"
            variant="outlined"
            type="text"
            value={username}
            onChange={e => setUsername(e.target.value)}
            required
          />
          <TextField
            name="password"
            id="outlined-basic"
            label="Password"
            variant="outlined"
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />
          {register && (
            <TextField
              name="password2"
              id="outlined-basic"
              label="Confirm Password"
              variant="outlined"
              type="password"
              value={password2}
              onChange={e => setPassword2(e.target.value)}
              required
            />
          )}
          <br />
          <br />
          <Button type="submit">{register ? 'Sign up' : 'Login'}</Button>
        </form>
        <button onClick={() => setRegister(!register)}>
          {register ? 'Already have account?' : 'Dont have an account yet?'}
        </button>
      </header>
    </div>
  );
};

export default Login;
